import * as THREE from 'three'
import { useRef, useState, useMemo, useEffect, Suspense } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Billboard, Text, TrackballControls } from '@react-three/drei'
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom'

// Import page components
import Home from './pages/Home'
import About from './pages/About'
import Skills from './pages/Skills'
import Projects from './pages/Projects'
import Contact from './pages/Contact'

// Import layout component
import Layout from './components/Layout'

// Create a wrapper component that can access the Router context
function NavigableWord({ children, path, isNavigable = false, ...props }) {
  const navigate = useNavigate()

  const color = new THREE.Color()
  const fontProps = { font: '/Inter-Bold.woff', fontSize: 2.5, letterSpacing: -0.05, lineHeight: 1, 'material-toneMapped': false }
  const ref = useRef()
  const [hovered, setHovered] = useState(false)
  const over = (e) => (e.stopPropagation(), setHovered(true))
  const out = () => setHovered(false)

  // Change the mouse cursor on hover
  useEffect(() => {
    if (hovered) document.body.style.cursor = 'pointer'
    return () => (document.body.style.cursor = 'auto')
  }, [hovered])

  // Handle navigation using React Router's navigate function
  const handleClick = () => {
    if (isNavigable && path) {
      navigate(path)
    }
  }

  // Tie component to the render-loop
  useFrame(({ camera }) => {
    // Make navigable words slightly larger and brighter
    const targetColor = hovered ? '#fa2720' : (isNavigable ? '#ffffff' : '#aaaaaa')
    const targetScale = isNavigable ? 1.0 : 0.8

    ref.current.material.color.lerp(color.set(targetColor), 0.1)
    ref.current.scale.lerp(new THREE.Vector3(targetScale, targetScale, targetScale), 0.1)
  })

  return (
    <Billboard {...props}>
      <Text 
        ref={ref} 
        onPointerOver={over} 
        onPointerOut={out} 
        onClick={handleClick} 
        {...fontProps} 
        children={children} 
      />
    </Billboard>
  )
}

function Cloud({ count = 4, radius = 20 }) {
  // Define navigation words and their paths
  const navWords = [
    { text: 'Home', path: '/' },
    { text: 'About', path: '/about' },
    { text: 'Skills', path: '/skills' },
    { text: 'Projects', path: '/projects' },
    { text: 'Contact', path: '/contact' }
  ]

  // Define additional decorative words that aren't navigable
  const decorativeWords = [
    'React', 'Three.js', 'WebGL', 'JavaScript', 'CSS3',
    'Portfolio', 'Developer', 'Creative', 'Interactive', '3D',
    'Design', 'Frontend', 'Web', 'Animation', 'Experience'
  ]

  // Create a distribution of words on a sphere
  const words = useMemo(() => {
    const temp = []
    const spherical = new THREE.Spherical()

    // Place navigation words in prominent positions (front hemisphere)
    const navPhiSpan = Math.PI / (navWords.length + 1)
    const navThetaSpan = Math.PI / navWords.length

    // Position the nav words in the front hemisphere for better visibility
    for (let i = 0; i < navWords.length; i++) {
      const phi = navPhiSpan * (i + 1)
      const theta = navThetaSpan * i + Math.PI / 4 // Offset to position in front
      const pos = new THREE.Vector3().setFromSpherical(spherical.set(radius, phi, theta))
      temp.push([pos, navWords[i].text, navWords[i].path, true])
    }

    // Distribute decorative words around the sphere
    const decPhiSpan = Math.PI / (decorativeWords.length / 3 + 1)
    const decThetaSpan = (Math.PI * 2) / (decorativeWords.length / 3)

    for (let i = 0; i < decorativeWords.length; i++) {
      // Calculate position to avoid overlap with nav words
      const phi = decPhiSpan * ((i % 5) + 1)
      const theta = decThetaSpan * (Math.floor(i / 5)) + Math.PI / 8 * (i % 3)

      // Check if this position is too close to any existing word
      const pos = new THREE.Vector3().setFromSpherical(spherical.set(radius, phi, theta))

      // Simple collision detection - ensure words aren't too close
      const tooClose = temp.some(([existingPos]) => existingPos.distanceTo(pos) < 5)

      if (!tooClose) {
        temp.push([pos, decorativeWords[i], null, false])
      } else {
        // Try a slightly different position
        const adjustedPos = new THREE.Vector3().setFromSpherical(
          spherical.set(radius, phi + 0.2, theta + 0.2)
        )
        temp.push([adjustedPos, decorativeWords[i], null, false])
      }
    }

    return temp
  }, [radius])

  return words.map(([pos, word, path, isNavigable], index) => (
    <Word 
      key={index} 
      position={pos} 
      path={path}
      isNavigable={isNavigable}
    >
      {word}
    </Word>
  ))
}

// Wrapper component for the 3D scene
function Scene() {
  return (
    <Canvas dpr={[1, 2]} camera={{ position: [0, 0, 35], fov: 90 }}>
      <fog attach="fog" args={['#202025', 0, 80]} />
      <Suspense fallback={null}>
        <group rotation={[10, 10.5, 10]}>
          <Cloud count={8} radius={20} />
        </group>
      </Suspense>
      <TrackballControls />
    </Canvas>
  )
}

// Main App component with routing
export default function App() {
  return (
    <Router>
      <div style={{ width: '100%', height: '100%', position: 'relative' }}>
        {/* The 3D scene is always rendered in the background */}
        <Scene />

        {/* Routes are rendered on top of the 3D scene */}
        <Routes>
          <Route path="/" element={<Layout><Home /></Layout>} />
          <Route path="/about" element={<Layout><About /></Layout>} />
          <Route path="/skills" element={<Layout><Skills /></Layout>} />
          <Route path="/projects" element={<Layout><Projects /></Layout>} />
          <Route path="/contact" element={<Layout><Contact /></Layout>} />
        </Routes>
      </div>
    </Router>
  )
}
