import * as THREE from 'three'
import { useRef, useState, useMemo, useEffect, Suspense, createContext, useContext } from 'react'
import { Canvas, useFrame } from '@react-three/fiber'
import { Billboard, Text, TrackballControls } from '@react-three/drei'
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom'

// Import page components
import Home from './pages/Home'
import About from './pages/About'
import Skills from './pages/Skills'
import Projects from './pages/Projects'
import Contact from './pages/Contact'

// Import layout component
import Layout from './components/Layout'

// Create a navigation context
const NavigationContext = createContext(null);

// Custom hook to use navigation
function useNavigation() {
  return useContext(NavigationContext);
}

// Navigation provider component
function NavigationProvider({ children }) {
  const navigate = useNavigate();
  const location = useLocation();

  return (
    <NavigationContext.Provider value={{ navigate, location }}>
      {children}
    </NavigationContext.Provider>
  );
}

// Word component for navigable words
function Word({ children, path, ...props }) {
  const { navigate } = useNavigation();
  const color = new THREE.Color()
  const fontProps = { font: '/Inter-Bold.woff', fontSize: 2.5, letterSpacing: -0.05, lineHeight: 1, 'material-toneMapped': false }
  const ref = useRef()
  const [hovered, setHovered] = useState(false)
  const over = (e) => (e.stopPropagation(), setHovered(true))
  const out = () => setHovered(false)

  // Change the mouse cursor on hover
  useEffect(() => {
    if (hovered) document.body.style.cursor = 'pointer'
    return () => (document.body.style.cursor = 'auto')
  }, [hovered])

  // Handle navigation using our custom navigation hook
  const handleClick = () => {
    if (path) {
      if (path.startsWith('http://') || path.startsWith('https://')) {
        // For external links, open in a new tab
        window.open(path, '_blank');
      } else {
        // For internal links, use our custom navigation
        console.log('Navigating to:', path);
        navigate(path);
      }
    }
  }

  // Tie component to the render-loop
  useFrame(({ camera }) => {
    // All words are now navigable, so they all use the same styling
    const targetColor = hovered ? '#fa2720' : '#ffffff'
    const targetScale = 1.0

    ref.current.material.color.lerp(color.set(targetColor), 0.1)
    ref.current.scale.lerp(new THREE.Vector3(targetScale, targetScale, targetScale), 0.1)
  })

  return (
    <Billboard {...props}>
      <Text 
        ref={ref} 
        onPointerOver={over} 
        onPointerOut={out} 
        onClick={handleClick} 
        {...fontProps} 
        children={children} 
      />
    </Billboard>
  )
}

function Cloud({ radius = 20 }) {
  // Define navigation words and their paths
  const navWords = [
    { text: 'Home', path: '/' },
    { text: 'About', path: '/about' },
    { text: 'Skills', path: '/skills' },
    { text: 'Projects', path: '/projects' },
    { text: 'Contact', path: '/contact' }
  ]

  // Create a distribution of words on a sphere
  const words = useMemo(() => {
    const temp = []
    const spherical = new THREE.Spherical()

    // Place navigation words in a more spaced out distribution
    const phiSpan = Math.PI / (navWords.length + 1)
    const thetaSpan = (Math.PI * 2) / navWords.length

    // Position the nav words evenly around the sphere - only one instance of each
    for (let i = 0; i < navWords.length; i++) {
      const phi = phiSpan * (i + 1)
      const theta = thetaSpan * i
      const pos = new THREE.Vector3().setFromSpherical(spherical.set(radius, phi, theta))
      temp.push([pos, navWords[i].text, navWords[i].path])
    }

    return temp
  }, [radius])

  return words.map(([pos, word, path], index) => (
    <Word 
      key={index} 
      position={pos} 
      path={path}
    >
      {word}
    </Word>
  ))
}

// Wrapper component for the 3D scene
function Scene() {
  return (
    <NavigationProvider>
      <Canvas dpr={[1, 2]} camera={{ position: [0, 0, 35], fov: 90 }}>
        <fog attach="fog" args={['#202025', 0, 80]} />
        <Suspense fallback={null}>
          <group rotation={[10, 10.5, 10]}>
            <Cloud radius={20} />
          </group>
        </Suspense>
        <TrackballControls />
      </Canvas>
    </NavigationProvider>
  )
}

// Main App component with routing
export default function App() {
  return (
    <Router>
      <div style={{ width: '100%', height: '100%', position: 'relative' }}>
        {/* Wrap the entire application with NavigationProvider */}
        <NavigationProvider>
          {/* Render the 3D scene once, outside of the routes */}
          <Scene />

          {/* Routes are rendered on top of the 3D scene */}
          <Routes>
            <Route path="/" element={
              <Layout><Home /></Layout>
            } />
            <Route path="/about" element={
              <Layout><About /></Layout>
            } />
            <Route path="/skills" element={
              <Layout><Skills /></Layout>
            } />
            <Route path="/projects" element={
              <Layout><Projects /></Layout>
            } />
            <Route path="/contact" element={
              <Layout><Contact /></Layout>
            } />
          </Routes>
        </NavigationProvider>
      </div>
    </Router>
  )
}
